var feedbackLink=document.querySelector(".feedback-button"),feedbackPopup=document.querySelector(".modal-feedback-reg"),feedbackClose=document.querySelector(".body"),feedbackLogin=FeedbackPopup.querySelector(".mod2-username"),feedbackMail=FeedbackPopup.querySelector(".mod2-usermail"),feedbackForm=FeedbackPopup.querySelector(".feedback-form"),feedbackComment=FeedbackPopup.querySelector(".mod2-comment");
feedbackLink.addEventListener("click",function(a){a.preventDefault();feedbackPopup.classList.add("modal-show");storage?(feedbackLogin.value=storage,feedbackMail.focus()):feedbackLogin.focus();storage?(feedbackMail.value=storage,FeedbackComment.focus()):(feedbackLogin.focus(),feedbackMail.focus())});feedbackClose.addEventListener("click",function(a){a.preventDefault();feedbackPopup.classList.remove("modal-show");feedbackPopup.classList.remove("modal-error")});
